mapper.select:

select 

,

from qrtz_triggers
where SCHED_NAME = #{schedName,jdbcType=VARCHAR}
  and TRIGGER_NAME = #{triggerName,jdbcType=VARCHAR}
  and TRIGGER_GROUP = #{triggerGroup,jdbcType=VARCHAR}

mapper.delete:

delete from qrtz_triggers
where SCHED_NAME = #{schedName,jdbcType=VARCHAR}
  and TRIGGER_NAME = #{triggerName,jdbcType=VARCHAR}
  and TRIGGER_GROUP = #{triggerGroup,jdbcType=VARCHAR}
